import React, { useState, useEffect } from 'react';
import { getTodos, addTodo, updateTodo, deleteTodo } from '../services/api';

// Helper to normalize a todo object
const normalizeTodo = (todo) => ({
    ...todo,
    name: todo.name ?? todo.title,
    isComplete: todo.isComplete ?? todo.iscompleted,
    completedAt: todo.completedAt // may be undefined
});

// Helper to normalize an array of todos
const normalizeTodos = (todos) => todos.map(normalizeTodo);

// Sort: unchecked first, then checked (newest checked at top)
const sortTodos = (todos) => {
    const unchecked = todos.filter(t => !t.isComplete);
    const checked = todos
        .filter(t => t.isComplete)
        .sort((a, b) => (b.completedAt || 0) - (a.completedAt || 0));
    return [...unchecked, ...checked];
};

const TodoList = () => {
    const [todos, setTodos] = useState([]);
    const [newTodo, setNewTodo] = useState('');

    useEffect(() => {
        const loadTodos = async () => {
            const fetchedTodos = await getTodos();
            setTodos(normalizeTodos(fetchedTodos));
        };
        loadTodos();
    }, []);

    const handleAddTodo = async () => {
        if (newTodo.trim()) {
            const addedTodo = await addTodo({ name: newTodo, isComplete: false });
            setTodos([...todos, normalizeTodo(addedTodo)]);
            setNewTodo('');
        }
    };

    const handleUpdateTodo = async (id, updatedFields) => {
        const todoToUpdate = todos.find(t => t.id === id);
        if (!todoToUpdate) return;

        let updatedTodo = {
            ...todoToUpdate,
            ...updatedFields
        };

        // If marking as complete, add a timestamp
        if (updatedFields.isComplete && !todoToUpdate.isComplete) {
            updatedTodo.completedAt = Date.now();
        }
        // If marking as incomplete, remove the timestamp
        if (!updatedFields.isComplete && todoToUpdate.isComplete) {
            updatedTodo.completedAt = undefined;
        }

        const result = await updateTodo(id, updatedTodo);

        // Always normalize the result
        const normalizedResult = normalizeTodo(result);

        setTodos(todos.map(t => t.id === id ? normalizedResult : t));
    };

    const handleDeleteTodo = async (id) => {
        await deleteTodo(id);
        setTodos(todos.filter(todo => todo.id !== id));
    };

    return (
        <div>
            <div className="input-row">
                <input
                    type="text"
                    value={newTodo}
                    onChange={(e) => setNewTodo(e.target.value)}
                    placeholder="Add a new todo"
                />
                {newTodo.trim() && (
                    <button
                        className="add-todo-button"
                        onClick={handleAddTodo}
                        aria-label="Add"
                        title="Add"
                    >
                        ➕
                    </button>
                )}
            </div>
            <ul>
                {sortTodos(todos).map(todo => (
                    <li key={todo.id} className="todo-list-item">
                        <input
                            type="checkbox"
                            checked={todo.isComplete}
                            onChange={() => handleUpdateTodo(todo.id, { isComplete: !todo.isComplete })}
                            aria-label={todo.isComplete ? 'Mark as incomplete' : 'Mark as complete'}
                            style={{ marginRight: '8px' }}
                        />
                        <span
                            className="todo-item-text"
                            style={{ textDecoration: todo.isComplete ? 'line-through' : 'none' }}
                        >
                            {todo.name}
                        </span>
                        <button
                            className="todo-item-button"
                            onClick={() => handleDeleteTodo(todo.id)}
                            aria-label="Delete"
                            title="Delete"
                        >
                            🗑️
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default TodoList;
