import React, { useEffect, useState } from 'react';
import { getTodos, addTodo, updateTodo, deleteTodo } from '../services/api';

const TodoList = () => {
    const [todos, setTodos] = useState([]);
    const [newTodo, setNewTodo] = useState('');

    useEffect(() => {
        const loadTodos = async () => {
            const fetchedTodos = await getTodos();
            setTodos(fetchedTodos);
        };
        loadTodos();
    }, []);

    const handleAddTodo = async () => {
        if (newTodo.trim()) {
            const addedTodo = await addTodo({ name: newTodo, isComplete: false });
            setTodos([...todos, addedTodo]);
            setNewTodo('');
        }
    };

    const handleUpdateTodo = async (id, updatedFields) => {
        // Find the existing todo by id
        const todoToUpdate = todos.find(t => t.id === id);
        if (!todoToUpdate) return;

        // Merge the existing todo with the updated fields
        const updatedTodo = {
            ...todoToUpdate,
            ...updatedFields
        };

        // Send the full updated todo object to the backend
        const result = await updateTodo(id, updatedTodo);

        // Update the local state
        setTodos(todos.map(t => t.id === id ? result : t));
    };

    const handleDeleteTodo = async (id) => {
        await deleteTodo(id);
        setTodos(todos.filter(todo => todo.id !== id));
    };

    return (
        <div>
            <h1>Todo List</h1>
            <input
                type="text"
                value={newTodo}
                onChange={(e) => setNewTodo(e.target.value)}
                placeholder="Add a new todo"
            />
            <button onClick={handleAddTodo}>Add</button>
            <ul>
                {todos.map(todo => (
                    <li key={todo.id}>
                        <span style={{ textDecoration: todo.isComplete ? 'line-through' : 'none' }}>
                            {todo.name}
                        </span>
                        <button onClick={() => handleUpdateTodo(todo.id, { isComplete: !todo.isComplete })}>
                            {todo.isComplete ? 'Undo' : 'Complete'}
                        </button>
                        <button onClick={() => handleDeleteTodo(todo.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default TodoList;
